            @Html.Partial("~/views/partials/navigation/topNavigation.cshtml")
        </nav>
    </div>
    <header class="header">
        <div class="logo">
            @if (home.SiteLogo != null && !string.IsNullOrEmpty(home.SiteLogo.Url))
            {
                <div class="nav-link--home">
